import React, { useState, useEffect, Component } from 'react';
import axios from 'axios'

function EditProfile() {

    const [username, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');


    function onChangeUsername(event) {
        this.setName({ username: event.target.value })
    }
    function onChangeEmail(event) {
        this.setEmail({ email: event.target.value })
    }
    function onChangePassword(event) {
        this.setPassword({ password: event.target.value })
    }



    function onSubmit(e) {
        e.preventDefault()
        const newUser = {
            username: this.state.username,
            email: this.state.email,
            password: window.btoa(this.state.password),
            address: this.state.address,
            phone: this.state.phone,
            type: this.state.type
        }
        axios.post('http://localhost:4000/user/register', newUser)
            .then(res => {
                if (res.status == 200) {
                    this.setState({
                        username: '',
                        email: '',
                        password: '',
                        address: '',
                        phone: '',
                        type: ''
                    })
                    document.getElementById("comments").innerHTML = "Success"
                    document.getElementById("comments").className = "alert alert-success alert-dismissible fade show"
                }
                else {
                    document.getElementById("comments").innerHTML = "Error"
                    document.getElementById("comments").className = "alert alert-danger alert-dismissible fade show"
                    console.log(res.data)
                    window.location.replace("/login")
                }
            })
            .catch(err => {
                document.getElementById("comments").innerHTML = "Error"
                document.getElementById("comments").className = "alert alert-danger alert-dismissible fade show"
            })
    }
    return (

            <div>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Username: </label>
                        <input type="text"
                            className="form-control"
                            required="true"
                            value={this.state.username}
                            onChange={this.onChangeUsername}
                        />
                    </div>
                    <div className="form-group">
                        <label>Password: </label>
                        <input type="password"
                            className="form-control"
                            required="true"
                            value={this.state.password}
                            onChange={this.onChangePassword}
                        />
                    </div>
                    <div className="form-group">
                        <label>Email: </label>
                        <input type="email"
                            className="form-control"
                            required="true"
                            value={this.state.email}
                            onChange={this.onChangeEmail}
                        />
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Create User" className="btn btn-primary" />
                    </div>
                    <div id="comments">
                    </div>
                </form >
            </div >
    );
}

export default EditProfile
