import React, { useState, useEffect, Component } from 'react';
import axios from 'axios'

function EditProfile() {

    const [ID, setID] = useState("");
    const [username, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [type, setType] = useState('')

    useEffect(() => {
        let a = localStorage.getItem('DASS_USERID')
        if (!a || a == 0|| a==""|| a=="0")
            setID("")
        else {
            setID(a)
            axios.get('http://localhost:4000/user/' + a)
                .then(response => {
                    setName(response.data.username)
                })
        }
    }
    )

    const onChangeUsername = (event) =>  {
        setName(event.target.value)
    }
    const onChangeEmail = (event) =>  {
        setEmail(event.target.value)
    }
    const onChangePassword = (event) =>  {
        setPassword(event.target.value)
    }
    const onChangeType = (event) =>  {
        let type_dd = document.getElementById("type_dd")
        setType(event.target.value)
    }


    const onSubmit = (e) => {
        e.preventDefault()
        const newUser = {
            _id: this.state.ID,
            username: this.state.username,
            email: this.state.email,
            password: window.btoa(this.state.password),
            type: this.state.type

        }
        axios.post('http://localhost:4000/user/update', newUser)
            .then(res => {
                if (res.status == 200) {
                    this.setState({
                        username: '',
                        email: '',
                        password: '',
                        type: 'Customer'

                    })
                    document.getElementById("comments").innerHTML = "Success"
                    document.getElementById("comments").className = "alert alert-success alert-dismissible fade show"
                }
                else {
                    document.getElementById("comments").innerHTML = "Error"
                    document.getElementById("comments").className = "alert alert-danger alert-dismissible fade show"
                    console.log(res.data)
                    window.location.replace("/login")
                }
            })
            .catch(err => {
                document.getElementById("comments").innerHTML = "Error"
                document.getElementById("comments").className = "alert alert-danger alert-dismissible fade show"
            })
    }
    return (

            <div>
                <form onSubmit={onSubmit}>
                    <div className="form-group">
                        <label>Username: </label>
                        <input type="text"
                            className="form-control"
                            required="true"
                            value={username}
                            onChange={onChangeUsername}
                        />
                    </div>
                    <div className="form-group">
                        <label>Password: </label>
                        <input type="password"
                            className="form-control"
                            required="true"
                            value={password}
                            onChange={onChangePassword}
                        />
                    </div>
                    <div className="form-group">
                        <label>Email: </label>
                        <input type="email"
                            className="form-control"
                            required="true"
                            value={email}
                            onChange={onChangeEmail}
                        />
                    </div>
                    <div className="form-group">
                        <label>Type: </label>
                        <select
                            id='type_dd'
                            className="form-control"
                            value={type}
                            onChange={onChangeType}
                        >
                            <option value='Vendor' selected>Vendor</option>
                            <option value='Customer' >Customer</option>
                        </select>
                    </div>
                    <div className="form-group">
                        <input type="submit" value="Create User" className="btn btn-primary" />
                    </div>
                    <div id="comments">
                    </div>
                </form >
            </div >
    );
}

export default EditProfile
