import React, { useState, useEffect, Component } from 'react';
import { useHistory } from "react-router-dom"
import axios from 'axios';
import { Button } from 'react-bootstrap';


function Home() {
    const [ID, setID] = useState("0");
    const [name, setName] = useState("");
    const [allProducts, setProducts] = useState("");
    const [allUsers, setAllUsers] = useState([])



    const history = useHistory();
    const handleClick = () => {
        history.push("/addproduct")
    }

    useEffect(() => {
        let a = localStorage.getItem('DASS_USERID')
        if (!a || a == 0)
            setID("0")
        else
            setID(a)
        axios.get('http://localhost:4000/user/' + a)
            .then(response => {
                console.log(response.data)
                setName(response.data.username)
            })
        axios.get('http://localhost:4000/product/vendor/' + a)
            .then(response => {
                setProducts(response.data)
                console.log(allProducts)
            })
            axios.get('http://localhost:4000/user/showall/')
            .then(response => {

                setAllUsers(response.data);

            })

    }
    )

    function getname(name) {
        if (name == "") {
            return ""
        }
        var results = allUsers.filter(function (i) {
            return i._id == name
        })
        if (results[0] == undefined) {
            return ""
        }
        return (results[0].username)
    }

    return (

        <div>
                <Button onClick={handleClick} value={handleClick}  variant='dark' type='button'>Add Food Item</Button>
          <br/><br/> <h1>Your Food Items:</h1>

          <table className="table table-hover thead-light table-responsive-lg ">
                    <thead>
                        <tr>
                            <th>Placed Time</th>
                            <th>Food Item</th>
                            <th>Quantity</th>
                            <th>Price</th>
                            <th>Customer</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            { order }.order.map((currentOrder, i) => {
                                return (
                                    <tr>
                                       <td>{currentOrder.time}</td>
                                       <td> {(() => getPname(currentOrder.product))()}</td>
                                    <td>{currentOrder.quantity}</td>
                                    <td>{currentOrder.cost}</td>
                                    <td>{(() => getname(currentOrder.customer))()}</td>
                                    <td>{currentOrder.status}</td>
                                        <td><Button variant="dark" onClick={() => viewMore(currentOrder._id)} value={currentOrder._id}>MOVE TO NEXT STAGE</Button></td>
                                    </tr>
                                )
                            })
                        }
                    </tbody>
                </table>
            </div>

    );
}

export default Home;
